mapper.select:
select
  PV.product_id AS id,
  PV.version AS version
from
    product_versions PV LEFT JOIN  products P ON PV.product_id=P.id
where
  P.app_id=#{app_id}
  AND P.category_id=#{category_id}
  AND PV.supported_mod=''
;

mapper.update:
UPDATE
  product_versions
SET
  supported_mod=#{supported_mod}
WHERE
  product_id=#{id} AND version=#{version}
;
