mapper.select:
SELECT SQL_CALC_FOUND_ROWS id, account_id, product_id, version, created_at, updated_at, message, device, rating FROM comment WHERE 1=1
and account_id=#{account_id}
and product_id=#{product_id}
and version=#{version}
LIMIT #{count}, #{pages};

mapper.update:
UPDATE comment
set
updated_at=#{updated_at},
message=#{message}

    ,rating=#{rating}


    ,device=#{device}

where
id = #{id};

mapper.select:
SELECT TRUNCATE(AVG(rating),1) as rating_version
  FROM comment
  WHERE product_id=#{product_id}
  and version=#{version}
UNION ALL
    SELECT
  TRUNCATE(AVG(rating),1) AS rating_product
FROM comment
WHERE product_id =#{product_id}
